import React, { useState } from 'react';
import { Link2, Copy, ExternalLink, Settings, Clock, QrCode } from 'lucide-react';
import toast from 'react-hot-toast';
import api from '../services/api';
import QRCodeGenerator from './QRCodeGenerator';

const UrlShortener = ({ onUrlCreated }) => {
  const [formData, setFormData] = useState({
    url: '',
    customCode: '',
    expiresIn: ''
  });
  const [isLoading, setIsLoading] = useState(false);
  const [result, setResult] = useState(null);
  const [showAdvanced, setShowAdvanced] = useState(false);
  const [showQR, setShowQR] = useState(false);

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!formData.url.trim()) {
      toast.error('Please enter a URL');
      return;
    }

    setIsLoading(true);
    try {
      const response = await api.shortenUrl(formData);
      setResult(response.data);
      toast.success('Link shortened successfully!');
      setFormData({ url: '', customCode: '', expiresIn: '' });
      if (onUrlCreated) onUrlCreated();
    } catch (error) {
      toast.error(error.response?.data?.error || 'An error occurred');
    } finally {
      setIsLoading(false);
    }
  };

  const handleCopy = (text) => {
    navigator.clipboard.writeText(text);
    toast.success('Copied!');
  };

  const handleInputChange = (field, value) => {
    setFormData(prev => ({ ...prev, [field]: value }));
  };

  return (
    <div className="space-y-6">
      {/* Main Form */}
      <div className="card">
        <div className="text-center mb-6">
          <div className="bg-primary-100 w-16 h-16 rounded-full flex items-center justify-center mx-auto mb-4 floating-icon glow-effect">
            <Link2 size={32} className="text-primary-600" />
          </div>
          <h2 className="text-2xl font-bold text-gray-800 mb-2">
            URL Shortener
          </h2>
          <p className="text-gray-600 dark:text-[#9CA3AF] mb-2">
            Transform your long URLs into short and shareable links
          </p>
        </div>

        <form onSubmit={handleSubmit} className="space-y-4">
          <div>
            <label className="block text-sm font-medium text-gray-700 dark:text-gray-300 mb-2">
              URL to Shorten
            </label>
            <input
              type="url"
              placeholder="https://example.com/very-long-url"
              value={formData.url}
              onChange={(e) => handleInputChange('url', e.target.value)}
              className="input"
              required
            />
          </div>

          {/* Advanced Options */}
          <div>
            <button
              type="button"
              onClick={() => setShowAdvanced(!showAdvanced)}
              className="flex items-center space-x-2 text-sm text-primary-600 hover:text-primary-700 transition-colors"
            >
              <Settings size={16} />
              <span>Advanced Options</span>
            </button>
          </div>

          {showAdvanced && (
            <div className="space-y-4 p-4 bg-gray-50 rounded-lg animate-slide-up">
              <div>
                <label className="block text-sm font-medium text-gray-700 dark:text-gray-300 mb-2">
                  Custom Short Code (Optional)
                </label>
                <input
                  type="text"
                  placeholder="my-custom-link"
                  value={formData.customCode}
                  onChange={(e) => handleInputChange('customCode', e.target.value)}
                  className="input"
                  pattern="[a-zA-Z0-9_-]+"
                  minLength="3"
                  maxLength="20"
                />
                <p className="text-xs text-gray-500 mt-1">
                  3-20 characters, only letters, numbers, _ and - allowed
                </p>
              </div>

              <div>
                <label className="block text-sm font-medium text-gray-700 dark:text-gray-300 mb-2">
                  <Clock size={16} className="inline mr-1" />
                  Expiration Time (Hours)
                </label>
                <select
                  value={formData.expiresIn}
                  onChange={(e) => handleInputChange('expiresIn', e.target.value)}
                  className="input"
                >
                  <option value="">No Expiration</option>
                  <option value="1">1 Hour</option>
                  <option value="24">1 Day</option>
                  <option value="168">1 Week</option>
                  <option value="720">1 Month</option>
                  <option value="8760">1 Year</option>
                </select>
              </div>
            </div>
          )}

          <button
            type="submit"
            disabled={isLoading}
            className="btn btn-primary w-full py-3 text-lg"
          >
            {isLoading ? (
              <div className="flex items-center justify-center space-x-2">
                <div className="w-5 h-5 border-2 border-white border-t-transparent rounded-full animate-spin"></div>
                <span>Shortening...</span>
              </div>
            ) : (
              'Shorten Link'
            )}
          </button>
        </form>
      </div>

      {/* Result */}
      {result && (
        <div className="card animate-slide-up">
          <div className="text-center mb-4">
            <div className="bg-green-100 w-12 h-12 rounded-full flex items-center justify-center mx-auto mb-3 animate-bounce-gentle">
              <Link2 size={24} className="text-green-600" />
            </div>
            <h3 className="text-lg font-semibold text-gray-800 mb-2">
              Link Shortened Successfully!
            </h3>
          </div>

          <div className="space-y-4">
            <div>
              <label className="block text-sm font-medium text-gray-700 dark:text-gray-300 mb-2">
                Shortened Link
              </label>
              <div className="flex">
                <input
                  type="text"
                  value={result.short_url}
                  readOnly
                  className="input rounded-r-none border-r-0"
                />
                <button
                  onClick={() => handleCopy(result.short_url)}
                  className="btn btn-secondary rounded-none border-l border-gray-300"
                  title="Copy URL"
                >
                  <Copy size={16} />
                </button>
                <button
                  onClick={() => setShowQR(true)}
                  className="btn btn-secondary rounded-none border-l border-gray-300"
                  title="Generate QR Code"
                >
                  <QrCode size={16} />
                </button>
                <a
                  href={result.short_url}
                  target="_blank"
                  rel="noopener noreferrer"
                  className="btn btn-primary rounded-l-none ml-1"
                  title="Open URL"
                >
                  <ExternalLink size={16} />
                </a>
              </div>
            </div>

            <div>
              <label className="block text-sm font-medium text-gray-700 dark:text-gray-300 mb-2">
                Original Link
              </label>
              <div className="p-3 bg-gray-50 rounded-lg text-sm text-gray-600 break-all">
                {result.original_url}
              </div>
            </div>

            {result.expires_at && (
              <div className="text-sm text-amber-600 bg-amber-50 p-3 rounded-lg">
                <Clock size={16} className="inline mr-1" />
                This link will expire on {new Date(result.expires_at).toLocaleString('en-US')}
              </div>
            )}
          </div>
        </div>
      )}
    </div>
  );
};

export default UrlShortener;
